int Nodes_len[0x10];
char *Nodes[0x10];
int count=0;
int get_atoi()
{
    char buf[8];
    read(0,buf,8);    
    return atoi(buf);
}
void sandbox(){
    struct sock_filter filter[] = {
    BPF_STMT(BPF_LD+BPF_W+BPF_ABS,4),
    BPF_JUMP(BPF_JMP+BPF_JEQ,0xc000003e,0,2),
    BPF_STMT(BPF_LD+BPF_W+BPF_ABS,0),
    BPF_JUMP(BPF_JMP+BPF_JEQ,59,0,1),
    BPF_STMT(BPF_RET+BPF_K,SECCOMP_RET_KILL),
    BPF_STMT(BPF_RET+BPF_K,SECCOMP_RET_ALLOW),
    };
    struct sock_fprog prog = {
    .len = (unsigned short)(sizeof(filter)/sizeof(filter[0])),
    .filter = filter,
    };
    prctl(PR_SET_NO_NEW_PRIVS,1,0,0,0);
    prctl(PR_SET_SECCOMP,SECCOMP_MODE_FILTER,&prog);
}
void add(){
    int len;
    printf("len:");
    scanf("%d",&len);
    if(len0xfff) exit(0);
    if(count>0x10){
        puts("too many");
        exit(0);
    }
    Nodes[count] = malloc(len);
    Nodes_len[count] = len;
    count++;
    puts("done!");
}

void del(){
    int idx;
    printf("idx:");
    scanf("%d",&idx);
    if(idx>count){
        puts("error!");
        exit(0);
    }
    free(Nodes[idx]);
    puts("done!");    
}

void edit(){
    int idx;
    printf("idx:");
    scanf("%d",&idx);
    if(idx>count){
        puts("error!");
        exit(0);
    }
    read(0,Nodes[idx],Nodes_len[idx]);
    puts("done!");
}
void show(){
    int idx;
    printf("idx:");
    scanf("%d",&idx);
    if(idx>count){
        puts("error!");
        exit(0);
    }
    write(1,Nodes[idx],Nodes_len[idx]);
}
void gift(){
    printf("heap_base:%p\n",Nodes[0]);
}
void menu(){
    puts("1.add");
    puts("2.delete");
    puts("3.edit");
    puts("4.show");
    puts("5.gift");
    puts("6.exit");
    printf("choice:");
}
void init() {
    setvbuf(stdin, 0LL, 2, 0LL);
    setvbuf(stdout, 0LL, 2, 0LL);
    setvbuf(stderr, 0LL, 2, 0LL);
}

int main(){
    init();
    sandbox();
    while(1){
        int choice;
        menu();
