    free(p1);
    p1[1] = &stack_buffer; // p1->bk = stack_buffer_addr
    stack_buffer[1] = 0x120; //stack_buffer->size = 0x120
    stack_buffer[3] = &stack_buffer; // stack_buffer->bk = stack_buffer_addr    
