    puts("done!");
}
void show(){
    int idx;
    printf("idx:");
    scanf("%d",&idx);
    if(idx>count){
        puts("error!");
        exit(0);
    }
    write(1,Nodes[idx],Nodes_len[idx]);
}
void gift(){
    printf("heap_base:%p\n",Nodes[0]);
}
void menu(){
    puts("1.add");
    puts("2.delete");
    puts("3.edit");
    puts("4.show");
    puts("5.gift");
    puts("6.exit");
    printf("choice:");
}
void init() {
    setvbuf(stdin, 0LL, 2, 0LL);
    setvbuf(stdout, 0LL, 2, 0LL);
    setvbuf(stderr, 0LL, 2, 0LL);
}

int main(){
    init();
    sandbox();
    while(1){
        int choice;
        menu();
        choice = get_atoi();
        switch(choice){
            case 1:
                add();
                break;
            case 2:
